->getTransactionType();        $balance = $Portfolio->getPortfolioBalance($allTransactions, $this->ttypeArr);        App::uses('CakeTime', 'Utility');        $tradeDateCondition = CakeTime::dayAsSql(date('d-m-Y'), 'Order.created');        App::uses('Order', 'Model');        $Order = new Order();        $allOrdersInfo = $Order->find('all', array(            'conditions' => "Order.user_id=$userId and $tradeDateCondition",            'recursive' => -1        ));        $Omo = $this->Components->load('Omo');        $userList = $Omo->getUsersList(2, 1);        $broker_fee = $userList[$userId]['brker_fee'];        $tradeData['balanceUsed'] = $Omo->getUsedBalance($allOrdersInfo, $broker_fee);        $tradeData['balance'] = $balance;        $jsonresult = json_encode($tradeData);        echo $jsonresult;        exit;    }    public function updatePurchasePower()    {        $add=$total=$deduct=0;        if (isset($_POST['userId'])) {            $userId = $_POST['userId'];            $portfolio_id = $userId; // as userid=portfolio_id        }        if (isset($_POST['add'])) {            $add = $_POST['add'];        }        if (isset($_POST['total'])) {            $total = $_POST['total'];        }        if (isset($_POST['deduct'])) {            $deduct = $_POST['deduct'];        }        $portfolioData = array();        App::uses('Portfolio', 'Model');        $Portfolio = new Portfolio();        $msg = '';        $portfolioData = $Portfolio->find('all', array(            'conditions' => "Portfolio.id=$portfolio_id",            'recursive' => -1        ));        $dataToSave=array();        $dataToSave[0]['id']=$portfolio_id;        if ($add) {            //$rate = $ordersInfo[0]['PortfolioTransaction']['rate'] + $add;            $dataToSave[0]['balance']=$portfolioData[0]['Portfolio']['balance']+ $add;            $msg = "$add Taka Added";        }        if ($deduct) {          //  $rate = $ordersInfo[0]['PortfolioTransaction']['rate'] - $deduct;            $dataToSave[0]['balance']=$portfolioData[0]['Portfolio']['balance']- $deduct;            $msg = "$deduct Taka deducted";        }        if ($total) {            $dataToSave[0]['balance']=$total;            $msg = "Total balanace set to $total Taka";        }        if ($Portfolio->saveMany($dataToSave)) {            $portfolioData = $Portfolio->find('all', array(                'conditions' => "Portfolio.id=$portfolio_id",                'recursive' => -1            ));            $balance=$portfolioData[0]['Portfolio']['balance'];            $msg = "$msg. New Balanace is = $balance Taka ";        } else {            $msg = "There is some problem";        }        $tradeData['balance'] = $balance;        $tradeData['msg'] = $msg;        $jsonresult = json_encode($tradeData);        echo $jsonresult;        exit;    }    public function updatePurchasePower_prev()    {        if (isset($_POST['userId'])) {            $userId = $_POST['userId'];            $portfolio_id = $userId; // as userid=portfolio_id        }        if (isset($_POST['add'])) {            $add = $_POST['add'];        }        if (isset($_POST['total'])) {            $total = $_POST['total'];        }        if (isset($_POST['deduct'])) {            $deduct = $_POST['deduct'];        }        $Portfolio = $this->Components->load('Portfolio');        $allTransactions = $Portfolio->getAllTransactions($portfolio_id);        $this->ttypeArr = $Portfolio->getTransactionType();        $balance = $Portfolio->getPortfolioBalance($allTransactions, $this->ttypeArr);        $portfolioTransactionData = array();        App::uses('PortfolioTransaction', 'Model');        $PortfolioTransaction = new PortfolioTransaction();        $ordersInfo = $PortfolioTransaction->find('all', array(            'conditions' => "PortfolioTransaction.portfolio_id=$userId and PortfolioTransaction.transaction_type_