         } else if ('B' == $cell->getColumn()) {                    $array_data[$rowIndex][$cell->getColumn()] = $cell->getCalculatedValue();                } else if ('C' == $cell->getColumn()) {                    $array_data[$rowIndex][$cell->getColumn()] = PHPExcel_Style_NumberFormat::toFormattedString($cell->getCalculatedValue(), 'YYYY-MM-DD');                } else if ('D' == $cell->getColumn()) {                    $array_data[$rowIndex][$cell->getColumn()] = $cell->getCalculatedValue();                }            }        }        pr($array_data);        exit;    }    public function pdf()    {        Configure::write('debug', 2);        require_once(APP . 'Vendor' . DS . 'class.pdf2text.php');        $a = new PDF2Text();        $a->setFilename('xls/test_data.pdf');        $a->decodePDF();        echo $a->output();        exit;    }    function scan_dir($dir)    {        $ignored = array('.', '..', '.svn', '.htaccess');        $files = array();        foreach (scandir($dir) as $file) {            if (in_array($file, $ignored)) continue;            $files[$file] = filemtime($dir . '/' . $file);        }        arsort($files);        $files = array_keys($files);        return ($files) ? $files : false;    }//UPDATE `omo_new2`.`orders` SET `order_status` = 'locked' WHERE `orders`.`id` = 50 Or `orders`.`id` = 49 Or `orders`.`id` = 48 Or `orders`.`id` = 47;//UPDATE `omo_new2`.`orders` SET `dse_order_id` = NULL  WHERE `orders`.`id` = 50 Or `orders`.`id` = 49 Or `orders`.`id` = 48 Or `orders`.`id` = 47;    public function execute_report()    {        Configure::write('debug', 2);        $broker_id = 11;        App::uses('CakeTime', 'Utility');        $tradeDateCondition = CakeTime::dayAsSql(date('d-m-Y'), 'Order.created');        App::uses('Order', 'Model');        $Order = new Order();        $allOrdersInfo = $Order->find('all', array(            'conditions' => "User.broker_id=$broker_id and $tradeDateCondition /*and Order.dse_order_id IS NULL*/",            'contain' => 'User.broker_id',            'recursive' => -1        ));        $dir = 'execution_report';        $fileArr = $this->scan_dir($dir);        //pr($fileArr);        $reportFile = $fileArr[0];        require_once(APP . 'Vendor' . DS . 'phpexcel' . DS . 'PHPExcel.php');        $objPHPExcel = new PHPExcel();        $objReader = new PHPExcel_Reader_Excel5();        $objReader->setReadDataOnly(true);        $objPHPExcel = $objReader->load("$dir/$reportFile");        $rowIterator = $objPHPExcel->getActiveSheet()->getRowIterator();        $array_data = array();        foreach ($rowIterator as $row) {            $cellIterator = $row->getCellIterator();            $cellIterator->setIterateOnlyExistingCells(false); // Loop all cells, even if it is not set            if (1 == $row->getRowIndex()) continue;//skip first row            $rowIndex = $row->getRowIndex();            //$array_data[$rowIndex] = array('A'=>'', 'B'=>'','C'=>'','D'=>'');            foreach ($cellIterator as $cell) {                if ('A' == $cell->getColumn()) {                    $array_data[$rowIndex]['client_id'] = $cell->getCalculatedValue();                } else if ('B' == $cell->getColumn()) {                    $array_data[$rowIndex]['instrument_code'] = $cell->getCalculatedValue();                } else if ('C' == $cell->getColumn()) {                    $array_data[$rowIndex]['day_initial_position'] = $cell->getCalculatedValue();                } else if ('D' == $cell->getColumn()) {                    $array_data[$rowIndex]['order_qty'] = $cell->getCalculatedValue();                } else if ('E' == $cell->getColumn()) {                    $array_data[$rowIndex]['executed_qty'] = $cell->getCalculatedValue();                } else if ('F' == $cell->getColumn()) {                    $array_data[$rowIndex]['avg_price'] = $cell->getCalculatedValue();                } else if ('G' == $cell->getColumn()) {                    $array_data[$rowIndex]['order_id'] = trim($cell->getCalculatedValue());                } else if ('H' == $cell->getColumn()) {                    $array_data[$rowIndex][$cell->getColumn()] = $cell->getCalculatedValue();                } else if ('I' == $cell->getColumn()) {                    $array_data[$rowIndex]['order_status'] = $cell->getCalculatedValue();                } else if ('J' == $cell->getColumn()) {                    $array_data[$rowIndex]['resulting_position'] = $cell->getCalculatedValue();                }            }        }        pr($array_data);        exit;        $validTransactionArr = array();        foreach ($array_data as $eachRow) {            $col_c = trim($eachRow['order_qty']);            if ($col_c != '') {                $validTransactionArr[] = $eachRow;            }        }        unset($validTransactionArr[0]);        $validTransactionArr = Hash::sort($validTransactionArr, "{n}.avg_price", 'ASC');        $Omo = $this->Components->load('Omo');        $allUsers = $Omo->getUsersList(1);        $allOrders = $Omo->get_broker_orders($allOrdersInfo, $broker_id);        $allLockedOrder = $allOrders['locked'];        $allExecutedOrder = $allOrders['executed'];        $validTransactionArr = $this->cleanTransaction($validTransactionArr, $allExecutedOrder);        $allLockedOrder = Hash::sort($allLockedOrder, "{n}.rate", 'ASC');        pr($allOrders);//        exit;        $dataToSave = array();        foreach ($allLockedOrder as $lockedOrder) {            $instrument_code = $lockedOrder['instrument_id'];            $user_id = $lockedOrder['user_id'];            $amount = $lockedOrder['amount'];            $rate = $lockedOrder['rate'];            $internal_ref_no = $allUsers[$broker_id][$user_id]['internal_ref_no'];            $allOrderByThisUser = Hash::extract($validTransactionArr, "{n}[client_id=/$internal_ref_no/]");            $allOrderOfThisInstrument = Hash::extract($allOrderByThisUser, "{n}[instrument_code=/$instrument_code/]");//  first process exact match            foreach ($allOrderOfThisInstrument as $row) {                $orderId = trim($row['order_id']);                $executedQty = $row['executed_qty'];                $order_qty = $row['order_qty'];                $order_id = $row['order_id'];                $avg_price = $row['avg_price'];                if ($amount == $order_qty and $amount == $executedQty and $rate == $avg_price) {                    $lockedOrder['instrument_id'] = $lockedOrder['ins_id'];                    $lockedOrder['order_status'] = 'executed';                    $lockedOrder['rate'] = $avg_price;                    $lockedOrder['dse_order_id'] = $order_id;                    $validTransactionArr = Hash::remove($validTransactionArr, "{n}[order_id=/$order_id/]");                    $dataToSave[] = $lockedOrder;                }            }// than process            foreach ($allOrderOfThisInstrument as $row) {                $orderId = trim($row['order_id']);                $executedQty = $row['executed_qty'];                $order_qty = $row['order_qty'];                $order_id = $row['order_id'];                $avg_price = $row['avg_price'];                if ($amount == $order_qty) {                    $lockedOrder['instrument_id'] = $lockedOrder['ins_id'];                    $lockedOrder['order_status'] = 'executed';                    $lockedOrder['rate'] = $avg_price;                    $lockedOrder['dse_order_id'] = $order_id;                }            }        }        if (count($dataToSave)) {            $Order->saveMany($dataToSave, array('atomic' => true));        }        exit;    }    public function all_orders_report()    {        Configure::write('debug', 2);        $broker_id = 11;        App::uses('CakeTime', 'Utility');        $tradeDateCondition = CakeTime::dayAsSql(date('d-m-Y'), 'Order.created');