           } else if ('F' == $cell->getColumn()) {                    $array_data[$rowIndex]['executed_qty'] = $cell->getCalculatedValue();                } else if ('G' == $cell->getColumn()) {                    $array_data[$rowIndex]['status'] = trim($cell->getCalculatedValue());                } else if ('H' == $cell->getColumn()) {                    $array_data[$rowIndex]['sub_status'] = $cell->getCalculatedValue();                } else if ('I' == $cell->getColumn()) {                    $array_data[$rowIndex]['remaining_qty'] = $cell->getCalculatedValue();                } else if ('J' == $cell->getColumn()) {                    $array_data[$rowIndex]['order_id'] = $cell->getCalculatedValue();                }            }        }        $validTransactionArr = array();        foreach ($array_data as $eachRow) {            $col_c = trim($eachRow['order_qty']);            if ($col_c != '') {                $validTransactionArr[] = $eachRow;            }        }        unset($validTransactionArr[0]);        $validTransactionArr = Hash::sort($validTransactionArr, "{n}.avg_price", 'ASC');        $Omo = $this->Components->load('Omo');        $allUsers = $Omo->getUsersList(4);        $allOrders = $Omo->get_broker_orders($allOrdersInfo, $broker_id);        $allLockedOrder = $allOrders['locked'];        $allExecutedOrder = $allOrders['executed'];        $allLockedOrder = Hash::sort($allLockedOrder, "{n}.rate", 'ASC');        $validTransactionArr = $this->cleanTransaction($validTransactionArr, $allExecutedOrder);        pr($validTransactionArr);//        exit;        $dataToSave = array();        foreach ($validTransactionArr as $row) {            $order_id = trim($row['order_id']);            $instrument_code = trim($row['instrument_code']);            $executed_qty = $row['executed_qty'];            $order_qty = $row['order_qty'];            $avg_price = $row['avg_price'];            $status = trim($row['status']);            $sub_status = trim($row['sub_status']);            $internal_ref_no = trim($row['client_id']);            if (isset($allUsers[$broker_id][$internal_ref_no]['id'])) {                $user_id = $allUsers[$broker_id][$internal_ref_no]['id'];                $allOrderByThisUser = Hash::extract($allLockedOrder, "{n}[user_id=$user_id]");                $allOrderOfThisInstrument = Hash::extract($allOrderByThisUser, "{n}[instrument_id=/$instrument_code/]");                if (!empty($allOrderOfThisInstrument)) {                    pr($allOrderOfThisInstrument);                    pr($row);                    if ($sub_status == 'Filled') { // if full executed                        foreach ($allOrderOfThisInstrument as $lockedOrder) {                            //  pr($row);                            //  pr($allOrderOfThisInstrument);                            $partial_amount = $lockedOrder['partial_amount'];                            $amount = $lockedOrder['amount'] + $partial_amount;                            $rate = $lockedOrder['rate'];                            if ($order_qty == $amount and $avg_price == $rate) {                                $lockedOrder['instrument_id'] = $lockedOrder['ins_id'];                                $lockedOrder['order_status'] = 'executed';                                $lockedOrder['rate'] = $avg_price;                                $lockedOrder['dse_order_id'] = $order_id;                                $validTransactionArr = Hash::remove($validTransactionArr, "{n}[order_id=/$order_id/]");                                $dataToSave[] = $lockedOrder;                            }                        }                    }                    if ($sub_status == 'Partially filled') { // if full executed                        foreach ($allOrderOfThisInstrument as $lockedOrder) {                            pr($row);                            pr($allOrderOfThisInstrument);                            $partial_amount = $lockedOrder['partial_amount'];                            $amount = $lockedOrder['amount'] + $partial_amount;                            $rate = $lockedOrder['rate'];                            if ($order_qty == $amount and $avg_price == $rate) {                                // updating un executed amount                                $lockedOrder['instrument_id'] = $lockedOrder['ins_id'];                                $lockedOrder['amount'] = $lockedOrder['amount'] - ($executed_qty - $partial_amount);                                $lockedOrder['partial_amount'] = $lockedOrder['partial_amount'] + ($executed_qty - $partial_amount);                                $dataToSave[] = $lockedOrder;                                // inserting new row into database with executed amount                                unset($lockedOrder['id']);                                $lockedOrder['instrument_id'] = $lockedOrder['ins_id'];                                $lockedOrder['order_status'] = 'executed';                                $lockedOrder['rate'] = $avg_price;                                $lockedOrder['amount'] = $executed_qty - $partial_amount;                                $lockedOrder['partial_amount'] = 0;                                //  $lockedOrder['dse_order_id'] = $order_id;                                $validTransactionArr = Hash::remove($validTransactionArr, "{n}[order_id=/$order_id/]");                                $dataToSave[] = $lockedOrder;                            }                        }                    }                    // exit;                }            }        }        pr($dataToSave);        //exit;        if (count($dataToSave)) {            $Order->saveMany($dataToSave, array('atomic' => true));        }        exit;    }    public function cleanTransaction($validTransactionArr, $allExecutedOrder)    {//pr($validTransactionArr);//pr($allExecutedOrder);