 $balance = $usr['balance'];                $ppower = $powerArr[$irn][0];                $ppower = (float)str_replace(',', '', $ppower);                if ($balance > $ppower) {                    $transaction_type_id = 13;  // adjustment withdraw;                } else {                    $transaction_type_id = 12; // adjustment deposit                }                $amount = abs($ppower - $balance);                //  echo "$irn=$ppower <br />";                $temp = array();                $temp['portfolio_id'] = $pid;                $temp['transaction_type_id'] = $transaction_type_id;                $temp['amount'] = 1;                $temp['rate'] = $amount;                $temp['transaction_time'] = date('Y-m-d H:i:s');                // update users table balance                $temp2 = array();                $temp2['id'] = $id;                $temp2['balance'] = $ppower;                if ($amount > 5)  // difference less than 5 is overlooked                {                    $dataToSave[] = $temp;                    $dataToSave2[] = $temp2;                }            }        }        // pr($dataToSave2);        //  exit;        $model = ClassRegistry::init('PortfolioTransaction');        $model2 = ClassRegistry::init('User');        if (count($dataToSave)) {            $model->saveMany($dataToSave, array('atomic' => true));            $model2->saveMany($dataToSave2, array('atomic' => true));            echo count($dataToSave) . ' balanace updated';        }        exit;    }    public function runTradeIns()    {        App::uses('CakeEmail', 'Network/Email');        Configure::write('debug', 2);        $file = fopen("tradeins/apex/trade.csv", "r");        $Omo = $this->Components->load('Omo');        $userList = $Omo->getUsersList(4);        $StockBangladesh = $this->Components->load('StockBangladesh');        $ip = $StockBangladesh->getIp();        $instrumentList = $StockBangladesh->ins